DROP TABLE IF EXISTS coctails.recipe;

CREATE TABLE coctails.recipe(
    id					SERIAL PRIMARY KEY,
    name				VARCHAR(64) NOT NULL,
	instructions		VARCHAR(1024) NOT NULL,
	iceStateId			SMALLINT NOT NULL,
	glasswareId			SMALLINT NOT NULL,
	ingredient1Id 		SMALLINT NOT NULL,
	ingredient2Id		SMALLINT NOT NULL,
	ingredient3Id		SMALLINT,
	ingredient4Id		SMALLINT,
	ingredient5Id		SMALLINT,
	ingredient6Id		SMALLINT,
	ingredient7Id		SMALLINT,
	ingredient8Id		SMALLINT,
	ingredient9Id		SMALLINT,
	ingredient10Id		SMALLINT,
	measure1Id			VARCHAR(32) NOT NULL,
	measure2Id			VARCHAR(32) NOT NULL,
	measure3Id			VARCHAR(32),
	measure4Id			VARCHAR(32),
	measure5Id			VARCHAR(32),
	measure6Id			VARCHAR(32),
	measure7Id			VARCHAR(32),
	measure8Id			VARCHAR(32),
	measure9Id			VARCHAR(32),
	measure10Id			VARCHAR(32)
);

