	measure5Id,
	measure6Id,
	measure7Id,
	measure8Id,
	measure9Id,
	measure10Id
) VALUES
	(1, '155 Belmont', 'Blend with ice. Serve in a wine glass. Garnish with carrot.', 1, 7, 25, 27, 2, 49, 81, NULL, NULL, NULL, NULL, '80 ml', '40 ml', '40 ml', '40 ml', '1 tk', NULL, NULL, NULL, NULL, NULL),
	(2, '57 Chevy with a White License Plate', '1. Fill a rocks glass with ice 2.add white creme de cacao and vodka 3.stir', 1, 2, 2, 15, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '30 ml', '30 ml', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(3, '9 1/2 Weeks', 'Combine all ingredients in glass mixer. Chill and strain into Cocktail glass. Garnish with sliced strawberry.', 1, 1, 1, 49, 5, 16, NULL, NULL, NULL, NULL, NULL, '60 ml', '30 ml', '15 ml', '5 ml', NULL, NULL, NULL, NULL, NULL, NULL),
	(4, 'A Gilligans Island', 'Shaken, not stirred!', 0, 1, 2, 47, 49, 51, NULL, NULL, NULL, NULL, NULL, '30 ml', '30 ml', '90 ml', '90 ml', NULL, NULL, NULL, NULL, NULL, NULL),
	(5, 'A True Amaretto Sour', 'Rub the rim of an old fashioned glass with lemon, and dip repeatedly into granulated sugar until it has a good "frosted" rim. Shake a jigger of Amaretto with the juice of 1/2 a lemon. Strain into glass and fill the class with ice. Garnish with a Marachino Cherry.', 1, 3, 4, 80, 84, NULL, NULL, NULL, NULL, NULL, NULL, '45 ml', '1/2 tk mahl', '1 tk', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(6, 'A.D.M. (After Dinner Mint)', 'shake vigorously', 0, 10, 35, 22, 2, 69, NULL, NULL, NULL, NULL, NULL, '25 ml', '15 ml', '15 ml', 'Täida klaas', NULL, NULL, NULL, NULL, NULL, NULL),
	(7, 'A1', 'Pour all ingredients into a cocktail shaker, mix and serve over ice into a chilled glass.', 1, 1, 23, 13, 55, 60, NULL, NULL, NULL, NULL, NULL, '70 ml', '40 ml', '10 ml', '5 ml', NULL, NULL, NULL, NULL, NULL, NULL),
	(8, 'Abbey Martini', 'Put all ingredients into a shaker and mix, then strain contents into a chilled cocktail glass.', 1, 1, 23, 31, 49, 45, NULL, NULL, NULL, NULL, NULL, '80 ml', '40 ml', '40 ml', '20 tilka', NULL, NULL, NULL, NULL, NULL, NULL),
	(9, 'Absolut Summertime', 'Add all ingredients except lemon to shaker filled with ice. Cover and shake vigorously. Strain contents into ice filled collins glass. Garnish with lemon.', 1, 1, 1, 63, 57, 66, 80, NULL, NULL, NULL, NULL, '45 ml', '25 ml', '90 ml', '15 ml', '1 viil', NULL, NULL, NULL, NULL, NULL),
	(10, 'Absolutely Fabulous', 'Mix the Vodka and Cranberry juice together in a shaker and strain into a glass. Top up with Champagne.', 0, 8, 2, 51, 40, NULL, NULL, NULL, NULL, NULL, NULL, '40 ml', '80 ml', 'Täida klaas', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(11, 'Absolutly Screwed Up', 'Shake it up it tasts better that way, but you can stir it if you want. 6 of those and you will be wasted for the rest of the night.', 0, 1, 1, 49, 6, 41, NULL, NULL, NULL, NULL, NULL, '40 ml', '40 ml', '40 ml', 'Täida klaas', NULL, NULL, NULL, NULL, NULL, NULL),
	(12, 'Ace', 'Shake all the ingredients in a cocktail shaker and ice then strain in a cold glass.', 1, 4, 23, 60, 72, 70, 74, NULL, NULL, NULL, NULL, '80 ml', '20 ml', '20 ml', '20 ml', '1/2 tk', NULL, NULL, NULL, NULL, NULL),
	(13, 'Adam & Eve', 'Shake together all the ingredients and strain into a cold glass.', 1, 1, 23, 38, 11, 55, NULL, NULL, NULL, NULL, NULL, '40 ml', '40 ml', '40 ml', '5 ml', NULL, NULL, NULL, NULL, NULL, NULL),
	(14, 'Addington', 'Mix both the vermouths in a shaker and strain into a cold glass. Top up with a squirt of Soda Water.', 1, 1, 31, 30, 57, NULL, NULL, NULL, NULL, NULL, NULL, '80 ml', '40 ml', 'Täida klaas', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(15, 'Addison', 'Shake together all the ingredients and strain into a cold glass.', 1, 4, 23, 32, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '60 ml', '60 ml', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(16, 'Addison Special', 'Combine ingredients in the order listed into a shaker. Fill half full with ice and shake well. Strain into glass with ice and garnish with a cherry and orange wedge.', 1, 3, 2, 60, 49, NULL, NULL, NULL, NULL, NULL, NULL, '40 ml', '15 ml', 'Täida klaas', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(17, 'Adios Amigos Cocktail', 'Shake together all the ingredients and strain into a cold glass.', 1, 4, 28, 30, 38, 23, 58, 52, 64, NULL, NULL, '40 ml', '20 ml', '20 ml', '20 ml', '20 ml', '10 ml', '10 ml', NULL, NULL, NULL),
	(18, 'Afterglow', 'Mix. Serve over ice.', 1, 2, 49, 48, 60, NULL, NULL, NULL, NULL, NULL, NULL, '4 osa', '4 osa', '1 osa', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(19, 'Alice Cocktail', 'Shake well, strain into a large cocktail glass.', 0, 1, 48, 60, 49, 68, NULL, NULL, NULL, NULL, NULL, '20 ml', '10 ml', '10 ml', '40 ml', NULL, NULL, NULL, NULL, NULL, NULL),
	(20, 'Apple Karate', 'Place all ingredients in the blender jar - cover and whiz on medium speed until well blended. Pour in one tall, 2 medium or 3 small glasses and drink up.', 0, 2, 56, 81, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '500 ml', '1 tk', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(21, 'Applejack', 'Add all ingredients into mixing glass, chill and strain into cocktail glass', 1, 1, 34, 18, 63, NULL, NULL, NULL, NULL, NULL, NULL, '30 ml', '15 ml', '60 ml', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(22, 'Arizona Stingers', 'Place ice cubes in the hurricane glass . Add the 2 HEAPING shots of Absolute Vodka (Note: You can add as many shots of Absolute as you want!) Fill the rest of glass with the Arizona Icetea with lemon. Stir to mix using a spoon. Drink up and enjoy!!!!!!!', 1, 5, 2, 67, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '80 ml', '360 ml', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(23, 'Arizona Twister', 'Just mix in the shots of rum, vodka, and tequila. Add splashes of the three juices, heavy on the pineapple. Top off with grenadine. Crushed ice should already be in glass. Top off the glass with a pineapple wedge.', 2, 5, 2, 26, 36, 49, 48, 73, 60, 76, NULL, '40 ml', '40 ml', '40 ml', '5 ml', '5 ml', '5 ml', '10 tilka', '1 viil', NULL, NULL),
	(24, 'Army special', 'Pour Vodka, Gin and lime cordial into glass, and top up with crushed ice. Wait for ice to melt slightly and sip without a straw.', 2, 1, 2, 23, 53, NULL, NULL, NULL, NULL, NULL, NULL, '30 ml', '30 ml', '45 ml', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(25, 'Aviation', 'Add all ingredients into cocktail shaker filled with ice. Shake well and strain into cocktail glass. Garnish with a cherry.', 1, 1, 23, 17, 55, NULL, NULL, NULL, NULL, NULL, NULL, '45 ml', '15 ml', '15 ml', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(26, 'Bora Bora', 'Prepare in a blender or shaker, serve in a highball glass on the rocks. Garnish with 1 slice of pineapple and one cherry.', 1, 2, 48, 54, 55, 60, NULL, NULL, NULL, NULL, NULL, '100 ml', '60 ml', '10 ml', '10 ml', NULL, NULL, NULL, NULL, NULL, NULL),
	(27, 'Cherry Electric Lemonade', 'Now stir vigorously and then pour over a large cup of ice. Now drink it with a straw and stir occasionally.', 1, 6, 23, 37, 2, 28, 6, 57, 61, 63, NULL, '30 ml', '30 ml', '30 ml', '30 ml', '30 ml', '90 ml', '30 ml', '30 ml', NULL, NULL),
	(28, 'Cosmopolitan', 'Add all ingredients into cocktail shaker filled with ice. Shake well and double strain into large cocktail glass. Garnish with lime wheel.', 1, 1, 1, 9, 51, 52, NULL, NULL, NULL, NULL, NULL, '35 ml', '5 ml', '60 ml', '5 ml', NULL, NULL, NULL, NULL, NULL, NULL),
	(29, 'Cosmopolitan Martini', 'Pour all ingredients in mixing glass half filled with ice, shake and strain into chilled Martini glass.', 1, 1, 2, 9, 51, 78, NULL, NULL, NULL, NULL, NULL, '30 ml', '15 ml', '5 ml', '1/2 tk mahl', NULL, NULL, NULL, NULL, NULL, NULL),
	(30, 'Cream Soda', 'Pour 1oz of Spiced Rum into a highball glass with ice. Fill with Ginger Ale.', 1, 2, 29, 41, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '30 ml', 'Täida klaas', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(31, 'Dark Caipirinha', 'Muddle the sugar into the lime wedges in an old-fashioned glass. Fill the glass with ice cubes. Pour the cachaca into the glass. Stir well.', 1, 2, 24, 86, 78, NULL, NULL, NULL, NULL, NULL, NULL, '70 ml', '10 ml', '1 tk', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(32, 'Dirty Martini', 'Pour the vodka, dry vermouth and olive brine into a cocktail shaker with a handful of ice and shake well. Rub the rim of a martini glass with the wedge of lemon. Strain the contents of the cocktail shaker into the glass and add the olive. A dirty Martini contains a splash of olive brine or olive juice and is typically garnished with an olive.', 1, 1, 2, 30, 75, 80, 79, NULL, NULL, NULL, NULL, '60 ml', '15 ml', '30 ml', '1 viil', '1 tk', NULL, NULL, NULL, NULL, NULL),
	(33, 'Duchamps Punch', 'Shake all ingredients.Double strain in a chilled double old fashioned glass with abig ice cube. Garnish with a couple of lavender sprigs', 1, 3, 39, 52, 59, 20, 45, 85, 82, NULL, NULL, '50 ml', '25 ml', '25 ml', '15 ml', '15 tilka', 'Näpuotsatäis', '2 sprigs', NULL, NULL, NULL),
	(34, 'Espresso Martini', 'Pour ingredients into shaker filled with ice, shake vigorously, and strain into chilled martini glass', 1, 4, 2, 14, 64, NULL, NULL, NULL, NULL, NULL, NULL, '50 ml', '10 ml', '10 tilka', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(35, 'Flaming Lamborghini', 'Pour the Sambuca and Kahlua into the Cocktail Glass and give the drinker a straw. Pour the Baileys and Blue Curacao into two sepsrate shot glasses either side of the cocktail glass. Set light the concotion in the cocktail glass and start to drink through the straw (this drink should be drunk in one) , as the bottom of the glass is reached put out the fire by pouring the Baileys and Blue Curacao into the cocktail glass and keep drinking till its all gone!!', 0, 1, 14, 19, 8, 7, NULL, NULL, NULL, NULL, NULL, '30 ml', '30 ml', '30 ml', '30 ml', NULL, NULL, NULL, NULL, NULL, NULL),
	(36, 'French Martini', 'Pour all ingredients into shaker with ice cubes. Shake well and strain into a chilled cocktail glass. Squeeze oil from lemon peel onto the drink.', 1, 1, 2, 21, 48, NULL, NULL, NULL, NULL, NULL, NULL, '45 ml', '15 ml', '15 ml', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(37, 'Gagliardo', 'Shake well and serve in a cocktail glass. This is a home cocktail of American/Internet Bar del Pozzo, Pavia, Italy.', 0, 1, 3, 55, 12, 62, NULL, NULL, NULL, NULL, NULL, '5 osa', '3 osa', '1 osa', '1 osa', NULL, NULL, NULL, NULL, NULL, NULL),
	(38, 'Gin Rickey', 'Half-fill a tall glass with ice. Mix the gin and Grenadine together and pour over the ice. Add the lime or lemon juice and top off with soda water. Decorate the glass with lime and/or lemon slices.', 1, 2, 23, 60, 80, 57, 78, NULL, NULL, NULL, NULL, '60 ml', '5 ml', '1/2 tk mahl', 'Täida klaas', 'Kaunistuseks', NULL, NULL, NULL, NULL, NULL),
	(39, 'Imperial Cocktail', 'Shake with ice and strain into cocktail glass.', 1, 1, 42, 23, 52, NULL, NULL, NULL, NULL, NULL, NULL, '40 ml', '20 ml', '40 ml', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(40, 'Irish Curdling Cow', 'Pour Irish Cream, Vodka, and Bourbon in a glass. Add some ice and mix in the orange juice.', 1, 2, 7, 33, 2, 49, NULL, NULL, NULL, NULL, NULL, '25 ml', '25 ml', '25 ml', '75 ml', NULL, NULL, NULL, NULL, NULL, NULL),
	(41, 'Jitterbug', 'Wet glass, dip rim in sugar. Then add Ice. Then add everything else. Its that simple!', 1, 1, 23, 2, 52, 60, 87, 64, 57, NULL, NULL, '90 ml', '45 ml', '40 ml', '20 tilka', 'Näpuotsatäis', '20 tilka', 'Täida klaas', NULL, NULL, NULL),
	(42, 'Lemon Drop', 'Shake and strain into a chilled cocktail glass rimmed with sugar.', 1, 1, 2, 9, 80, NULL, NULL, NULL, NULL, NULL, NULL, '60 ml', '60 ml', '1 viilu mahl', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(43, 'Malibu Twister', 'Add rum & trister then, add cranberry jucie,stir', 0, 2, 26, 50, 51, NULL, NULL, NULL, NULL, NULL, NULL, '2 osa', '2 osa', '1 osa', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(44, 'Manhattan', 'Stirred over ice, strained into a chilled glass, garnished, and served up.', 1, 1, 33, 31, 45, 84, 77, NULL, NULL, NULL, NULL, '75 ml', '25 ml', '10 tilka', '1 tk', '1 keerd', NULL, NULL, NULL, NULL, NULL),
	(45, 'Martinez Cocktail', 'Stir all ingredients (except cherry) with ice and strain into a cocktail glass. Top with the cherry and serve.', 1, 1, 23, 30, 6, 46, 84, NULL, NULL, NULL, NULL, '30 ml', '30 ml', '1/4 tl', '10 tilka', '1 tk', NULL, NULL, NULL, NULL, NULL),
	(46, 'Martini', 'Straight: Pour all ingredients into mixing glass with ice cubes. Stir well. Strain in chilled martini cocktail glass. Squeeze oil from lemon peel onto the drink, or garnish with olive.', 1, 1, 23, 30, 79, NULL, NULL, NULL, NULL, NULL, NULL, '50 ml', '10 ml', '1 tk', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(47, 'Mary Pickford', 'Shake and strain into a chilled large cocktail glass', 1, 1, 25, 17, 60, 48, 84, NULL, NULL, NULL, NULL, '45 ml', '1/2 tl', '1/2 tl', '30 ml', '1 tk', NULL, NULL, NULL, NULL, NULL),
	(48, 'Midnight Mint', 'If available, rim cocktail (Martini) glass with sugar syrup then dip into chocolate flakes or powder. Add ingredients into shaker with ice. Shake well then strain into cocktail glass.', 1, 1, 7, 22, 71, NULL, NULL, NULL, NULL, NULL, NULL, '30 ml', '25 ml', '20 ml', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(49, 'Mojito', 'Muddle mint leaves with sugar and lime juice. Add a splash of soda water and fill the glass with cracked ice. Pour the rum and top with soda water. Garnish and serve with straw.', 1, 2, 25, 78, 87, 83, 57, NULL, NULL, NULL, NULL, '75 ml', '1 tk mahl', '10 ml', '4 lehte', NULL, NULL, NULL, NULL, NULL, NULL),
	(50, 'Mojito #3', 'Put mint with lemon juice in a glas, mash the mint with a spoon, ice, rum & fill up with club soda. Top it with Angostura.', 1, 1, 27, 55, 57, 45, 83, NULL, NULL, NULL, NULL, '125 ml', '30 ml', '125 ml', '8 tilka', '1/2 peotäit', NULL, NULL, NULL, NULL, NULL),
	(51, 'New York Lemonade', 'Serve in a chilled cocktail glass. Lemon and sugar the rim. Stir and Strain.', 1, 1, 1, 13, 53, 57, NULL, NULL, NULL, NULL, NULL, '60 ml', '30 ml', '60 ml', '30 ml', NULL, NULL, NULL, NULL, NULL, NULL),
	(52, 'Old Fashioned', 'Place sugar cube in old fashioned glass and saturate with bitters, add a dash of plain water. Muddle until dissolved. Fill the glass with ice cubes and add whiskey. Garnish with orange twist, and a cocktail cherry.', 1, 3, 33, 45, 58, 87, NULL, NULL, NULL, NULL, NULL, '45 ml', '15 tilka', '10 tilka', '5 g', NULL, NULL, NULL, NULL, NULL, NULL),
	(53, 'Orangeade', 'Place some ice cubes in a large tumbler or highball glass, add lemon juice, orange juice, sugar syrup, and stir well. Top up with cold soda water, serve with a drinking straw.', 1, 2, 49, 55, 64, 57, NULL, NULL, NULL, NULL, NULL, '150 ml', '50 ml', '25 ml', 'Täida klaas', NULL, NULL, NULL, NULL, NULL, NULL),
	(54, 'Pisco Sour', 'Vigorously shake and strain contents in a cocktail shaker filled with ice cubes, then pour into glass and garnish with bitters.[1]', 1, 1, 39, 55, 87, NULL, NULL, NULL, NULL, NULL, NULL, '60 ml', '30 ml', '20 ml', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(55, 'Rail Splitter', 'Mix sugar syrup with lemon juice in a tall glass filled with ice, Fill up with ginger ale.', 1, 2, 55, 41, 64, NULL, NULL, NULL, NULL, NULL, NULL, '20 ml', 'Täida klaas', '10 ml', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(56, 'Ruby Tuesday', 'Pour gin and cranberry into a highball filled with ice cubes. Add grenadine and stir.', 1, 2, 23, 51, 60, NULL, NULL, NULL, NULL, NULL, NULL, '60 ml', '150 ml', '10 ml', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(57, 'Shark Attack', 'Mix lemonade and water according to instructions on back of can. If the instructions say to add 4 1/3 cans of water do so. Mix into pitcher. Add 1 1/2 cup of Vodka (Absolut). Mix well. Pour into glass of crushed ice. Excellent!', 2, 9, 2, 65, 58, NULL, NULL, NULL, NULL, NULL, NULL, '375 ml', '250 ml', '750 ml', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(58, 'Zorbatini', 'Prepare like a Martini. Garnish with a green olive.', 0, 4, 2, 44, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '35 ml', '5 ml', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(59, 'Tequila Sunrise', 'Pour the tequila and orange juice into glass over ice. Add the grenadine, which will sink to the bottom. Stir gently to create the sunrise effect. Garnish and serve.', 1, 2, 37, 49, 60, NULL, NULL, NULL, NULL, NULL, NULL, '60 ml', '120 ml', '15 ml', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(60, 'The Evil Blue Thing', 'Pour ingredients into glass, and drop in a blue whale! The blue whale isnt really necessary, but it makes the drink more "fun".', 0, 3, 10, 8, 25, NULL, NULL, NULL, NULL, NULL, NULL, '45 ml', '30 ml', '15 ml', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(61, 'Vesper', 'Shake over ice until well chilled, then strain into a deep goblet and garnish with a thin slice of lemon peel.', 1, 1, 23, 2, 43, NULL, NULL, NULL, NULL, NULL, NULL, '60 ml', '15 ml', '10 ml', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(62, 'Yellow Bird', 'Shake and strain into a chilled cocktail glass', 1, 1, 28, 12, 6, 52, NULL, NULL, NULL, NULL, NULL, '30 ml', '15 ml', '15 ml', '15 ml', NULL, NULL, NULL, NULL, NULL, NULL)
;

SELECT setval(pg_get_serial_sequence('coctails.recipe', 'id'), coalesce(max(id),0) + 1, false) FROM coctails.recipe;
